import axios from "axios";

export function getData() {
  axios
    .post("http://cms.avertra.com/auth/local", {
      identifier: "Cms@avertra.com",
      password: "Avertra@2021",
    })
    .then((response) => {
      setToken(response.data.jwt);
      const emp_url = "http://cms.avertra.com/employees";

      const headers = { Authorization: "Bearer " + app.state.token };
      axios
        .get(emp_url, {
          headers: headers,
        })
        .then((response) => {
          app.setState({ employees: response.data });
        })
        .catch((error) => {
          console.log("An error occurred:", error.response);
        });
    });
}
