import * as fetchApi from "../helpers/Api/fetchDataApi.js";
import * as helperApi from "../helpers/Api/";
import store from "../index";
// action Creators
export const getTeams = () => async (dispatch) => {
  try {
    const data = await fetchApi.fetchTeams(store.getState().token);
    dispatch({ type: "FETCH_ALL_TEAMS", payload: data });
  } catch (err) {
    console.log(err);
  }
};
export const AddTeam = (NewTeamName) => async (dispatch) => {
  try {
    const data = await helperApi.addNewTeam(NewTeamName);
    dispatch({ type: "UPDATE_TEAMS", payload: data });
  } catch (err) {
    console.log(err);
  }
};
