import React from "react";
import { useDrop, useDrag } from "react-dnd";
import { addEmployeeToTeam } from "../helpers/functionallity/employeeTeam.js";
import { deleteTeam } from "../actions/teams";
import { useDispatch } from "react-redux";
import Card from "@mui/material/Card";
import { Button } from "@mui/material";
import CardHeader from "@mui/material/CardHeader";
import Add from "@material-ui/icons/Add";

function Team(props) {
  const dispatch = useDispatch();

  const [{ isOver }, drop] = useDrop(() => ({
    accept: "employee",
    drop: (item) => {
      addEmployeeToTeam(item.employee, props.team);
    },
  }));
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "team",
    item: { team: props.team },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));
  return (
    <Card
      className="teams"
      key={props.team.id}
      ref={drop}
      ref={drag}
      style={{ backgroundColor: "rgba(255, 255, 255, 0.3)" }}
    >
      <CardHeader title={props.team.Name} />
      <button
        onClick={() => {
          dispatch(deleteTeam(props.team, props.teams));
        }}
      >
        X
      </button>
      <Button
        variant={"contained"}
        fullWidth
        style={{
          height: "30px",
          opacity: "0.4",
          margin: "1vh",
        }}
        onClick={() => {
          dispatch(deleteTeam(props.team, props.teams));
        }}
      >
        <Typography
          variant={"button"}
          style={{ fontSize: 19, paddingRight: 5 }}
        >
          Add Team
        </Typography>
        <Add style={{ position: "absolute", bottom: 4, right: 670 }} />
      </Button>
    </Card>
  );
}

export default Team;
