import * as fetchApi from "../helpers/Api/fetchDataApi.js";
import * as functionallity from "../helpers/functionallity/addNewTeam";
import * as helaperApi from "../helpers/Api/helperApis";

import store from "../index";
// action Creators
export const getTeams = () => async (dispatch) => {
  try {
    const data = await fetchApi.fetchTeams(store.getState().token);
    dispatch({ type: "FETCH_ALL_TEAMS", payload: data });
  } catch (err) {
    console.log(err);
  }
};
export const AddTeam = (teams, NewTeamName) => async (dispatch) => {
  try {
    const newTeam = await helaperApi.createNewTeam(NewTeamName);
    console.log(newTeam, "cccccccccccccc", NewTeamName);
    newTeam = JSON.parse(localStorage.getItem("newTeam"));
    console.log(
      newTeam,
      "newTeammmmmmmmmmmmcsssssssssssssssssssmmmmmmmmmm",
      NewTeamName
    );

    dispatch({ type: "UPDATE_TEAMS", payload: teams.concat(newTeam) });
  } catch (err) {
    console.log(err);
  }
};
