import React from "react";
import { useDrag } from "react-dnd";
import Avatar from "@mui/material/Avatar";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import Team from "./Team";
import { Container, Col } from "react-bootstrap";
import { connect } from "react-redux";

function Employee(props) {
  console.log(props, "EMPLOYEE PROPS")
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "employee",
    item: {
      employee: props.state.employees.find((e) => e.id === props.employee.id),
    },

    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));
  const style = {
    backgroundColor: "rgba(255, 255, 255, 0.3)",
    borderRadius: "20px",
  };
  return (
    <Card className="employee" ref={drag} style={style}>
      <CardHeader
        avatar={
          <Avatar
            src={
              props.state.employees.find((e) => e.id === props.employee.id)[
                "Avatar"
              ]["url"]
            }
          />
        }
        title={
          props.state.employees.find((e) => e.id === props.employee.id).Name
        }
      />
      <Container className="teamsInAlliance">
        {props.state.employees
          .find((e) => e.id === props.employee.id)
          .teams.map((team) => {
             return <div key={team.id}>{team.Name}</div>;
          })}
      </Container>
    </Card>
  );
}

const mapStateToProps = (state, ownProps) => {
   return {
    state: state,
  };
};
export default connect(mapStateToProps)(Employee);
