import React, { useEffect } from "react";
import { getEmployees } from "./actions/employees";
import { getToken } from "./actions/token";
import { getTeams } from "./actions/teams";
import { getAlliances } from "./actions/alliances";
import { useDispatch } from "react-redux";
import { HTML5Backend } from "react-dnd-html5-backend";
import { DndProvider } from "react-dnd";
import DragDrop from "./components/DragDrop";
import "./styles/App.css";

function App() {
  const dispatch = useDispatch();

  useEffect(async () => {
    try {
      const token = await dispatch(getToken());
      const employees = await dispatch(getEmployees());
      const teams = await dispatch(getTeams());
      const alliances = await dispatch(getAlliances());
      slidesPlugin();
    } catch (err) {
      console.log(err);
    }
  }, [dispatch]);

  function slidesPlugin(activeSlide = 2) {
    const slides = document.querySelectorAll(".slide");
    console.log(slides, "SLIDES");
    slides[activeSlide].classList.add("active");

    for (const slide of slides) {
      slide.addEventListener("click", () => {
        clearActiveClasses();

        slide.classList.add("active");
      });
    }

    function clearActiveClasses() {
      slides.forEach((slide) => {
        slide.classList.remove("active");
      });
    }
  }

  return (
    <div>
      <div className="container">
        <div
          className="slide"
          style={{
            "background-image":
              "url('https://images.unsplash.com/photo-1477764250597-dffe9f601ae8?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=2000&q=80')",
          }}
        >
          <h3>Tiger</h3>
        </div>
        <div
          className="slide"
          style={{
            "background-image":
              "url('https://images.unsplash.com/photo-1477764250597-dffe9f601ae8?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=2000&q=80')",
          }}
        >
          <h3>Leopard</h3>
        </div>
        <div
          className="slide"
          style="
          background-image: url('https://images.unsplash.com/photo-1470848051974-964b789cb6fa?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1000&q=80');
        "
        >
          <h3>Lion</h3>
        </div>
        <div
          className="slide"
          style="
          background-image: url('https://images.unsplash.com/photo-1526289636136-bce3fd37f962?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1000&q=80');
        "
        >
          <h3>Snowleopard</h3>
        </div>
        <div
          className="slide"
          style="
          background-image: url('https://images.unsplash.com/photo-1515970501993-dafc9f582f3e?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1000&q=80');
        "
        >
          <h3>Puma</h3>
        </div>
      </div>
    </div>
  );
}

export default App;
