import React, { useEffect, useState } from "react";
import { getEmployees } from "./actions/employees";
import { getToken } from "./actions/token";
import { getTeams } from "./actions/teams";
import { getAlliances } from "./actions/alliances";
import { useDispatch } from "react-redux";
import { HTML5Backend } from "react-dnd-html5-backend";
import { DndProvider } from "react-dnd";
import DragDrop from "./components/DragDrop";
import "./styles/App.css";

function App() {
  const dispatch = useDispatch();
  const [token, setToken] = useState("");
  const [employees, setEmployees] = useState([]);
  const [teams, setTeams] = useState([]);
  const [alliances, setAlliances] = useState([]);
  useEffect(async () => {
    try {
      const token = await dispatch(getToken());
      setToken(token);
      const employees = await dispatch(getEmployees());
      setEmployees(employees);
      const teams = await dispatch(getTeams());
      setTeams(teams);
      const alliances = await dispatch(getAlliances());
      setAlliances(alliances);
    } catch (err) {
      console.log(err);
    }
  }, [dispatch]);

  return (
    <div>
      <DndProvider backend={HTML5Backend}>
        <div className="App">
          <DragDrop className="dnd" employee token alliances teams />
        </div>
      </DndProvider>
    </div>
  );
}

export default App;
