import React, { useEffect } from "react";
import { getEmployees } from "./actions/employees";
import { getToken } from "./actions/token";
import { getTeams } from "../actions/teams";
import { getAlliances } from "../actions/alliances";
import { useDrop, useDrag } from "react-dnd";
import { addEmployeeToTeam } from "../actions/employees";
import { deleteTeam } from "../actions/teams";
import { useDispatch } from "react-redux";
import Card from "@mui/material/Card";
import { Button } from "@mui/material";
import CardHeader from "@mui/material/CardHeader";
import DeleteTwoToneIcon from "@mui/icons-material/DeleteTwoTone";

function Team(props) {
  useEffect(async () => {
    try {
      const token = await dispatch(getToken());
      const employees = await dispatch(getEmployees());
      const teams = await dispatch(getTeams());
      const alliances = await dispatch(getAlliances());
    } catch (err) {
      console.log(err);
    }
  }, [dispatch]);
  const dispatch = useDispatch();
  console.log(props.teamEmployees, "teamEmployees");
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "employee",
    drop: (item) => {
      console.log("Employee Droped");
      dispatch(addEmployeeToTeam(item.employee, props.team, props.employees));
    },
  }));
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "team",
    item: { team: props.team },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  return (
    <div ref={drop}>
      <Card
        className="teams"
        key={props.team.id}
        ref={drag}
        style={{
          backgroundColor: "rgba(255, 255, 255, 0.3)",
          borderRadius: "20px",
        }}
      >
        <CardHeader title={props.team.Name} />

        <Button
          style={{
            opacity: "0.4",
            margin: "1vh",
          }}
          onClick={() => {
            dispatch(deleteTeam(props.team, props.teams));
          }}
        >
          <DeleteTwoToneIcon
            style={{
              position: "absolute",
              bottom: 55,
              right: -60,
              color: "red",
            }}
          />
        </Button>
      </Card>
    </div>
  );
}

export default Team;
