import React, { useEffect, useState } from "react";
import { useDrop, useDrag } from "react-dnd";
import { addTeamToAlliance } from "../actions/teams";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import store from "../index";
import { Container, Col } from "react-bootstrap";
import Stack from "@mui/material/Stack";
import { styled } from "@mui/material/styles";
import Paper from "@mui/material/Paper";

import { getTeamBelongToThisAlliance } from "../helpers/Api/helperApis.js";
import { useDispatch } from "react-redux";
import TeamInAlliance from "./TeamInAlliance";
function Alliance(props) {
  const dispatch = useDispatch();
  const [teams, setTeams] = useState(props.teams);

  useEffect(async () => {
    try {
      setTeams(await getTeamBelongToThisAlliance(props.alliance));
    } catch (err) {
      console.log(err);
    }
  }, [teams]);

  const [{ isOver }, drop] = useDrop(() => ({
    accept: "team",
    drop: (item) => {
      dispatch(addTeamToAlliance(item.team, props.alliance, teams));
    },
  }));
  const Item = styled(Paper)(({ theme }) => ({
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: "center",
    color: theme.palette.text.secondary,
  }));
  return (
    <List className="alliance" ref={drop}>
      {teams.map((team) => {
        return (
          <Stack
            direction={{ xs: "column", sm: "row" }}
            spacing={{ xs: 1, sm: 2, md: 4 }}
          >
            <Item>
              <TeamInAlliance key={team.id} team={team} />
            </Item>
            <Item>
              <TeamInAlliance key={team.id} team={team} />
            </Item>
          </Stack>
        );
      })}
    </List>
  );
}

export default Alliance;
