import React, { useEffect } from "react";
import { useDrag } from "react-dnd";
import Avatar from "@mui/material/Avatar";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";

function Employee(props) {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "employee",
    item: { employee: props.employee, employees: props.employees },

    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));
  const style = {
    backgroundColor: "rgba(255, 255, 255, 0.3)",
    borderRadius: "20px",
  };

  useEffect(async () => {
    const carouselSlider = (function () {
      let _slideIndex = 1;

      const _slides = document.querySelectorAll(".image-fade");
      const _dots = document.querySelectorAll(".dot");

      function _sliderInitState(slides, dots) {
        for (let i = 0; i < slides.length; i++) {
          slides[i].style.display = "none";
        }

        for (let i = 0; i < dots.length; i++) {
          dots[i].classList.remove("active");
        }
      }

      function _checkSlideIndexBoundary(slideIndex, slides) {
        if (slideIndex > slides.length) {
          _slideIndex = 1;
        }
        if (slideIndex === 0) {
          _slideIndex = slides.length;
        }
      }

      function _slideSelectionIndecator(dots, slideIndex) {
        dots[slideIndex - 1].classList.add("active");
      }

      function _activateSlide(slides, slideIndex) {
        slides[slideIndex - 1].style.display = "block";
        _slideSelectionIndecator(_dots, _slideIndex);
      }

      function _imageDirection(direction) {
        _sliderInitState(_slides, _dots);
        if (direction == "next") {
          _activateSlide(_slides, _slideIndex);
          _slideIndex++;
          _checkSlideIndexBoundary(_slideIndex, _slides);
        } else {
          _slideIndex--;
          _checkSlideIndexBoundary(_slideIndex, _slides);
          _activateSlide(_slides, _slideIndex);
        }
      }

      const _previousButton = document.querySelector(".previous");
      const _nextButton = document.querySelector(".next");

      _previousButton.addEventListener("click", function () {
        _imageDirection("previous");
      });

      _nextButton.addEventListener("click", function () {
        _imageDirection("next");
      });

      const slide = function () {
        _sliderInitState(_slides, _dots);
        _activateSlide(_slides, _slideIndex);
        _slideIndex++;
        _checkSlideIndexBoundary(_slideIndex, _slides);
        // Change image every 5 seconds
        setTimeout(slide, 5000);
      };

      return {
        slide,
      };
    })();
    carouselSlider.slide();
  });

  return (
    <div>
      <div class="container">
        <div class="navigation">
          <div class="previous btn-nav">a </div>
          <div class="next btn-nav">a</div>
        </div>
        <div class="sarousel-container">
          <div class="image-fade fade">
            <img
              src="https://cdn.pixabay.com/photo/2021/10/03/04/21/woman-6676901_960_720.jpg"
              style={{ width: "100%" }}
            ></img>
            <div class="caption-text">Image caption 1</div>
          </div>

          <div class="image-fade fade">
            <img
              src="https://cdn.pixabay.com/photo/2021/08/23/04/02/woman-6566600_960_720.jpg"
              style={{ width: "100%" }}
            ></img>
            <div class="caption-text">Image caption 2</div>
          </div>

          <div class="image-fade fade">
            <img
              src="https://cdn.pixabay.com/photo/2020/09/26/14/27/sparrow-5604220_960_720.jpg"
              style={{ width: "100%" }}
            ></img>
            <div class="caption-text">Image caption 3</div>
          </div>

          <div class="image-fade fade">
            <img
              src="https://cdn.pixabay.com/photo/2020/05/19/09/21/flower-5189902_960_720.jpg"
              style="width:100%"
            ></img>
            <div class="caption-text">Image caption 4</div>
          </div>
        </div>
      </div>

      <div style="text-align:center">
        <span class="dot"></span>
        <span class="dot"></span>
        <span class="dot"></span>
        <span class="dot"></span>
      </div>
    </div>
  );
}

export default Employee;
