import React, { useState, useEffect } from "react";
import Employee from "./Employee";
import Team from "./Team";
import Alliance from "./Alliance";
import { useSelector, useDispatch } from "react-redux";
import TextField from "@material-ui/core/TextField";
import { Button, Card } from "@mui/material";
import { AddTeam } from "../actions/teams";
import Grid from "@mui/material/Grid";
import GridList from "@material-ui/core/GridList";
import Slider from "react-slick";

// Import css files
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "../styles/App.css";
function DragDrop() {
  const dispatch = useDispatch();
  const teams = useSelector((state) => {
    return state.teams;
  });
  var t = teams.filter((t) => {
    return t.alliance === undefined;
  });
  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  console.log(teams, "TEAMS Drag and DROP");
  console.log(t, "t Drag and DROP");
  const [searchField, setsearchField] = useState("");
  const [NewTeamName, setNewTeamName] = useState("");
  const employees = useSelector((state) => state.employees);
  const alliances = useSelector((state) => state.alliances);
  var filteredEmployees = employees.filter((emp) =>
    emp["Name"].toLowerCase().includes(searchField.toLowerCase())
  );

  filteredEmployees = filteredEmployees
    .concat(filteredEmployees)
    .concat(filteredEmployees);
  console.log(filteredEmployees, "eeeeeeeeeee");
  return (
    <Grid
      container
      direction="column"
      justifyContent="space-around"
      alignItems="stretch"
    >
      <Grid item xs>
        <Slider {...settings}>
          {movie.map(function (movie) {
            return (
              <React.Fragment>
                <Link to={`/movies/${movie.id}`}>
                  <Card>
                    <Card.Img
                      variant="top"
                      src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                    />
                    <Card.Body>
                      <span>{movie.title}</span>
                    </Card.Body>
                  </Card>
                </Link>
              </React.Fragment>
            );
          })}
        </Slider>
      </Grid>
      <div>
        <Grid item xs>
          <TextField
            id="standard-basic"
            label="Team Name"
            variant="standard"
            value={NewTeamName}
            onChange={(e) => {
              setNewTeamName(e.target.value);
            }}
          />
          <Button
            onClick={() => {
              dispatch(AddTeam(teams, NewTeamName));
              setNewTeamName("");
            }}
          >
            Add Team
          </Button>
        </Grid>
        <Grid item xs>
          {t.map((team) => {
            return <Team key={team.id} team={team} />;
          })}
        </Grid>
      </div>
      <div>
        {alliances.map((alliance) => {
          return (
            <Alliance key={alliance.id} alliance={alliance} teams={teams} />
          );
        })}
      </div>
      {/* </div> */}
    </Grid>
  );
}

export default DragDrop;
