import React, { useState } from "react";
import Employee from "./Employee";
import Team from "./Team";
import Alliance from "./Alliance";
import { useDrop } from "react-dnd";
import { useSelector } from "react-redux";
import TextField from "@material-ui/core/TextField";

import "../styles/App.css";
function DragDrop() {
  const [searchField, setsearchField] = useState("");
  const employees = useSelector((state) => state.employees);
  const teams = useSelector((state) => state.teams);
  const alliances = useSelector((state) => state.alliances);
  console.log(alliances);
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "employee",
    drop: (item) => addEmployeeToTeam(item),
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  }));
  const filteredEmployees = employees.filter((emp) =>
    emp["Name"].toLowerCase().includes(searchField.toLowerCase())
  );

  const addEmployeeToTeam = (item) => {
    console.log(item, "IDL ");
    console.log(teams);
  };
  return (
    <div>
      <TextField
        id="standard-basic"
        label="Search Employee"
        variant="standard"
        onChange={(e) => setsearchField(e.target.value)}
      />
      <div className="employees">
        {filteredEmployees.map((employee) => {
          return <Employee id={employee.id} employee={employee} />;
        })}
      </div>
      <div className="teams" ref={drop}>
        {teams.map((team) => {
          return <Team id={team.id} team={team} />;
        })}
      </div>
      <div className="alliances">
        {alliances.map((alliance) => {
          return <Alliance id={alliance.id} alliance={alliance} />;
        })}
      </div>
    </div>
  );
}

export default DragDrop;
